import React, {Component} from 'react';
import config from 'uiconfigdata/indexConfig'
import Head from 'next/head'

import './index.less';

export default class Home extends Component {
  constructor(props = {}) {
    super(props);
    const solutionList = ((props.pageJson || {}).solution || {}).list || [];
    this.state = {
      solutionItem: solutionList[0],
      solutionList,
      search: '',
    };
  }

  componentDidMount() {
    $(window).scrollTop(0);
    this.autoSwiper();
    this.autoSwiper2();
  }

  autoSwiper = () => {
    const bannerSwiper = new Swiper('.swiperBanner.swiper-container', {
      lazy: {
        loadPrevNext: true,
      },
      initialSlide: 0,
      speed: 500,
      pagination: {
        el: '.swiper-pagination',
        clickable: true,
      },
      autoplay: {
        disableOnInteraction: true,
        delay: 5000,
      },
      loop: true, // 循环模式选项
    });
    const comtainer = $('.swiperBanner.swiper-container')[0];
    comtainer.onmousemove = function () {
      bannerSwiper.autoplay.stop();
    };
    comtainer.onmouseleave = function () {
      bannerSwiper.autoplay.start();
    };
  };
  autoSwiper2 = () => {
    const bannerSwiper = new Swiper('.swiperBanner2.swiper-container', {
      lazy: {
        loadPrevNext: true,
      },
      initialSlide: 0,
      speed: 500,
      pagination: {
        el: '.swiper-pagination',
        clickable: true,
      },
      autoplay: {
        disableOnInteraction: true,
        delay: 5000,
      },
      loop: true, // 循环模式选项
    });
    const comtainer = $('.swiperBanner2.swiper-container')[0];
    comtainer.onmousemove = function () {
      bannerSwiper.autoplay.stop();
    };
    comtainer.onmouseleave = function () {
      bannerSwiper.autoplay.start();
    };
  };
  solutionChange = index => {
    const {solutionList} = this.state
    solutionList.forEach((v, i) => {
      v.active = false;
    });
    solutionList[index].active = true;
    this.setState({
      solutionItem: solutionList[index],
      solutionList,
    });
  };


  render() {
    const {search,solutionList, solutionItem} = this.state;
    const {solution, businessList = [], bannerList = [], customer} = this.props.pageJson || {}
    return (
      <div className="anti-fraud-home-body">
        <Head>
          <title>小盾安全官网</title>
        </Head>
        <div className="swiperBanner banner swiper-container">
          <div className="swiper-wrapper">
            {bannerList.map((v, i) =>
              <div key={i} className="swiper-slide">
                {v.cdn ? <img data-src={`${v.url}`} className="swiper-lazy banner-bg"/> :
                  <img data-src={`${v.url}`} className="swiper-lazy"/>}
                {v.link && <a className="carousel-link" href={`${v.link}${search}`} target="_blank"/>}
                {v.text && <img className="text-content" src={v.text} />  }
                {v.img && v.img.map((item,index) =><img key={index} className={`banner-img speed_${(item.rotate ||{}).speed || 0} direction_${(item.rotate ||{}).direction || 0}`} src={item.url} />)}
                <div className="swiper-lazy-preloader"/>
              </div>)}
          </div>
          <div className="swiper-pagination"/>
        </div>

        <section className="product-serve">
          <div>
            <div className="section-title">
              产品与服务
              <div className="section-subtitle">深入业务场景，守护客户业务安全</div>
            </div>
            <div className="product-serve-con clearfix">
              {businessList.map((val, ind) => (
                <div key={ind} className="business-area">
                  <div className="i">
                    <img src={val.img} alt=""/>
                  </div>
                  <div className="title">{val.name}</div>
                  {val.spanList.map((v, i) => (
                    <a className="link" href={'/product/' + v.url + search} key={i}>
                      {v.label}
                    </a>
                  ))}
                  <a className="button" href={val.url + search}>
                    了解详情
                    <svg className="right-icon"
                         viewBox="0 0 1280 1024" version="1.1" xmlns="http://www.w3.org/2000/svg" p-id="2208">
                      <path
                        d="M740.07331363 141.33502221c13.09501624 0 24.03945947 4.38766503 32.96928381 13.39164711l324.44000245 324.316406C1106.41860453 488.05323768 1110.84952799 498.9976809 1110.84952799 512c0 13.1320951-4.43092346 24.07653833-13.3669281 32.95692468l-324.44000245 324.44000244c-8.93600464 9.01634193-19.87426758 13.3916471-32.96928381 13.3916471-13.2618711 0-24.34844947-4.37530518-33.11759996-13.1320951-8.80622864-8.88656592-13.22479224-19.96078515-13.22479225-33.21647668 0-13.0023191 4.48036218-23.94676232 13.37928796-32.95692467L952.66494265 558.34857177H230.24520364c-12.79220581 0-23.69338989-4.51126075-32.77153038-13.5214231C188.40789262 535.69338989 183.87809244 524.87872338 183.87809244 512c0-12.74894738 4.52980018-23.68721032 13.59558082-32.70355224C206.55799333 470.16268897 217.45917741 465.65142823 230.23902408 465.65142823h722.42591857L707.11020938 220.51692223c-8.89892578-8.88038635-13.37928796-19.95460487-13.37928796-32.95692467 0-13.2618711 4.4185636-24.33609033 13.22479225-33.08670068C715.72486344 145.58673096 726.81144182 141.21142578 740.07331363 141.21142578v0.12359643z"
                        p-id="2209"/>
                    </svg>
                  </a>
                </div>
              ))}
            </div>
          </div>
        </section>

        <section className="case-section">
          <img src={solutionItem.bg} alt=""/>
          <div>
            <div className="section-title">
              {solution.title}
              <div className="section-subtitle">  {solution.subtitle}</div>
            </div>

            <div className="overflow-div-sty clearfix" target="first-section">
              {solutionList.map((v, i) => (
                <div
                  key={i}
                  className={v.active ? 'section-area active' : 'section-area'}
                  onClick={() => {
                    this.solutionChange(i);
                  }}
                  onMouseEnter={() => {
                    this.solutionChange(i);
                  }}
                >{v.name}</div>
              ))}
            </div>
            <section className="first-section">
              <div className="content-area">
                <div className="icon"><img src={solutionItem.img} alt=""/></div>
                <div className="con">
                  <div className="title">{solutionItem.title}</div>
                  <div className="dec">{solutionItem.content}</div>
                </div>
              </div>
            </section>
          </div>
        </section>

        <section className="customer-section">
          <div>
            <div className="section-title">
              {customer.title}
              <div className="section-subtitle">
                {customer.subtitle}
              </div>
            </div>
            <div className="swiperBanner2 swiper-container">
              <div className="swiper-wrapper">
                {customer.img.map((v, i) =>
                  <div key={i} className="swiper-slide">
                    <img data-src={`${v.url}`} className="swiper-lazy"/>
                    <div className="swiper-lazy-preloader"/>
                  </div>)}
              </div>
              <div className="swiper-pagination"/>
            </div>
          </div>
        </section>
      </div>
    );
  }
}
