import App from 'next/app';
import React, { Component } from 'react';
import Head from 'next/head';
import PcLayout from 'components/layout';
import commProps from './common/commProps';

const debug = require('debug')('app:App');

class Layout extends Component {
  render() {
    const { children } = this.props;
    return (<PcLayout>{children}</PcLayout>);
  }
}

class InitApp extends App {
  static async getInitialProps({ Component, router, ctx }) {
    /**
     * 进入匹配的路由组件，判断是服务端渲染还是客户端渲染
     * 组件的公共数据往后透传_document和对应的app
     * 命令路由的组件和app之间共享props
     * 如果页面有getInitialProps函数，要先执行页面的getInitialProps，然后当做pageProps传递给页面
     */
    let pageProps = {};

    if (Component.getInitialProps) {
      pageProps = await Component.getInitialProps(ctx);
    }

    if (ctx && !ctx.req) {
      //客户端执行
      debug('Exce In %s', 'client');
    } else {
      //服务端执行
      debug('Exce In is %s', 'server');
    }

    /**
     * 公共属性获取
     * @type {string}
     */
    const { isMobile } = commProps(ctx);

    pageProps.isMobile = isMobile;

    return { pageProps };
  }

  render() {
    const { Component, pageProps } = this.props;

    // Component代指页面组件，就是pages下面的页面组件，将getInitialProps中pageProps传递给每个页面
    return (<Layout>
        <Head>
          <title>小盾安全</title>
          <meta name="keywords" content="同盾反欺诈,安全反欺诈,反欺诈服务,反欺诈产品,内容安全保护,接口安全保护,智能验证码,设备指纹,IP画像,注册登录保护,支付安全保护,营销活动保护" />
          <meta name="description" content="同盾科技反欺诈安全服务，为企业提供业务安全/内容安全/风控数据及风控工具，涉及的业务场景包括：注册登录保护/内容安全检测过滤/接口安全保护/交易支付保护/内容安全检测过滤等。" />
        </Head>
        <Component {...pageProps} />
      </Layout>);
  }
}

export default withReduxStore(InitApp);
